const Standard20TokenMock = require("@frontier-token-research/trl-contracts-poc/build/contracts/Standard20TokenMock");
const OwnedRegistry = require("@frontier-token-research/trl-contracts-poc/build/contracts/OwnedRegistry.json");
const TRL = require("@frontier-token-research/trl-contracts-poc/build/contracts/TRL.json");

export function abi(contractName: any) {
  switch (contractName) {
    case "Standard20TokenMock":
      return Standard20TokenMock.abi;
    case "TRL":
      return TRL.abi;
    case "OwnedRegistry":
      return OwnedRegistry.abi;
    default:
      return null;
  }
}

export function address(contractName: any) {
  switch (contractName) {
    case "Standard20TokenMock":
      //@IDEA return Standard20TokenMock.newtorks.address;
      return "0x3a1f6b0992b1a3f2a15f52099cf172e9bea930de";
    case "TRL":
       //@IDEA TRL.newtorks.address;
       if(process.env.INTEGRATION_TEST === '1'){
         const fs = require('fs');
         let addressFromFile = JSON.parse(fs.readFileSync(process.env.PROXY_ADDR_PATH));
         return addressFromFile.address;  
       }
       else{
         return "0x8b4746f55d8a32600510629d3557c90d2d3c471e";
       }
    case "CandidatesRegistry":
      return "0x26ade0ccab9860934169bf5dc65beac6a36a43ef"
      case "VotersRegistry":
      return "0xfac79eee311c76325bc3f618fb8554678a512bc7";
    default:
      return "0x";
  }
}